mapper.select:
	SELECT 
			TAR.ID,
			TAR.ATTACHMENTID,
			TAR.REFID,
			TAR.REFTYPE
	  FROM ATTA_ATTACHMENT_REF TAR
	
		  
            AND TAR.ID = #{id,javaType=java.lang.String}
        
		  
            AND TAR.ATTACHMENTID = #{attachmentId,javaType=java.lang.String}
        
		  
            AND TAR.REFID = #{refId,javaType=java.lang.String}
        
		  
            AND TAR.REFTYPE = #{refType,javaType=com.tx.component.attachment.model.AttachmentRefTypeEnum}
        
	
	  
          
            ORDER BY #{orderSql}
        
          
            ORDER BY ID

	SELECT COUNT(1)
	  FROM ATTA_ATTACHMENT_REF TAR
	
		  
            AND TAR.ID = #{id,javaType=java.lang.String}
        
		  
            AND TAR.ATTACHMENTID = #{attachmentId,javaType=java.lang.String}
        
		  
            AND TAR.REFID = #{refId,javaType=java.lang.String}
        
		  
            AND TAR.REFTYPE = #{refType,javaType=com.tx.component.attachment.model.AttachmentRefTypeEnum}
