			TA.LASTUPDATEOPERATORID,
			TA.LASTUPDATEDATE,
			TA.FILEID,
			TA.PARENTID,
			TA.FILENAME,
			TA.FILENAMEEXTENSION,
			TA.CREATEDATE,
			TA.PREID
	  FROM ATTA_ATTACHMENT TA
	
		  
            AND TA.ID = #{id,javaType=java.lang.String}
        
		  
            AND TA.DELETEOPERATORID = #{deleteOperatorId,javaType=java.lang.String}
        
		  
            AND TA.CREATEOPERATORID = #{createOperatorId,javaType=java.lang.String}
        
		  
            AND TA.DELETEDATE = #{deleteDate,javaType=java.util.Date}
        
		  
            AND TA.NEXTID = #{nextId,javaType=java.lang.String}
        
		  
            AND TA.SERVICETYPE = #{serviceType,javaType=com.tx.component.attachment.model.AttachmentServiceTypeEnum}
        
		  
            AND TA.LASTUPDATEOPERATORID = #{lastUpdateOperatorId,javaType=java.lang.String}
        
		  
            AND TA.LASTUPDATEDATE = #{lastUpdateDate,javaType=java.util.Date}
        
		  
            AND TA.FILEID = #{fileId,javaType=java.lang.String}
        
		  
            AND TA.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TA.FILENAME = #{filename,javaType=java.lang.String}
        
		  
            AND TA.FILENAMEEXTENSION = #{filenameExtension,javaType=java.lang.String}
        
		  
            AND TA.CREATEDATE = #{createDate,javaType=java.util.Date}
        
		  
            AND TA.PREID = #{preId,javaType=java.lang.String}
        
	
	  
          
            ORDER BY #{orderSql}
        
          
            ORDER BY ID

