mapper.select:
	SELECT 
			TFD.ID,
			TFD.SAVEPATH,
			TFD.CREATEOPERATORID,
			TFD.ENCODING,
			TFD.FILENAME,
			TFD.VCID,
			TFD.ORGANIZATIONID,
			TFD.SYSTEMID,
			TFD.TYPE,
			TFD.CREATEDATE,
			TFD.SERVICETYPE,
			TFD.LASTUPDATEOPERATORID,
			TFD.LASTUPDATEDATE
	  FROM CORE_FILE_DEFINITION TFD
	
		  
            AND TFD.ID = #{id,javaType=java.lang.String}
        
		  
            AND TFD.SAVEPATH = #{savePath,javaType=java.lang.String}
        
		  
            AND TFD.CREATEOPERATORID = #{createOperatorId,javaType=java.lang.String}
        
		  
            AND TFD.ENCODING = #{encoding,javaType=java.lang.String}
        
		  
            AND TFD.FILENAME = #{filename,javaType=java.lang.String}
        
		  
            AND TFD.VCID = #{vcid,javaType=java.lang.String}
        
		  
            AND TFD.ORGANIZATIONID = #{organizationId,javaType=java.lang.String}
        
		  
            AND TFD.SYSTEMID = #{systemId,javaType=java.lang.String}
        
		  
            AND TFD.TYPE = #{type,javaType=java.lang.String}
        
		  
            AND TFD.CREATEDATE = #{createDate,javaType=java.util.Date}
        
		  
            AND TFD.SERVICETYPE = #{serviceType,javaType=java.lang.String}
        
		  
            AND TFD.LASTUPDATEOPERATORID = #{lastUpdateOperatorId,javaType=java.lang.String}
        
		  
            AND TFD.LASTUPDATEDATE = #{lastUpdateDate,javaType=java.util.Date}
        
	
	  
          
            ORDER BY #{orderSql}
        
          
            ORDER BY ID

	SELECT COUNT(1)
	  FROM CORE_FILE_DEFINITION TFD
	
		  
            AND TFD.ID = #{id,javaType=java.lang.String}
        
		  
            AND TFD.SAVEPATH = #{savePath,javaType=java.lang.String}
        
		  
            AND TFD.CREATEOPERATORID = #{createOperatorId,javaType=java.lang.String}
        
		  
            AND TFD.ENCODING = #{encoding,javaType=java.lang.String}
        
		  
            AND TFD.FILENAME = #{filename,javaType=java.lang.String}
        
		  
            AND TFD.VCID = #{vcid,javaType=java.lang.String}
        
		  
            AND TFD.ORGANIZATIONID = #{organizationId,javaType=java.lang.String}
        
		  
            AND TFD.SYSTEMID = #{systemId,javaType=java.lang.String}
        
		  
            AND TFD.TYPE = #{type,javaType=java.lang.String}
        
		  
            AND TFD.CREATEDATE = #{createDate,javaType=java.util.Date}
        
		  
            AND TFD.SERVICETYPE = #{serviceType,javaType=java.lang.String}
        
		  
            AND TFD.LASTUPDATEOPERATORID = #{lastUpdateOperatorId,javaType=java.lang.String}
        
		  
            AND TFD.LASTUPDATEDATE = #{lastUpdateDate,javaType=java.util.Date}
