mapper.select:
	SELECT 
			TFD.ID,
			TFD.SYSTEM,
			TFD.MODULE,
			TFD.DELETEDATE,
			TFD.RELATIVEPATH,
			TFD.FILENAMEEXTENSION,
			TFD.FILENAME,
			TFD.CREATEDATE,
			TFD.LASTUPDATEDATE
	  FROM CORE_FILE_DEFINITION TFD
	
		  
            AND TFD.ID = #{id,javaType=java.lang.String}
        
		  
            AND TFD.SYSTEM = #{system,javaType=java.lang.String}
        
		  
            AND TFD.MODULE = #{module,javaType=java.lang.String}
        
		  
            AND TFD.DELETEDATE = #{deleteDate,javaType=java.util.Date}
        
		  
            AND TFD.RELATIVEPATH = #{relativePath,javaType=java.lang.String}
        
		  
            AND TFD.FILENAMEEXTENSION = #{filenameExtension,javaType=java.lang.String}
        
		  
            AND TFD.FILENAME = #{filename,javaType=java.lang.String}
        
		  
            AND TFD.CREATEDATE = #{createDate,javaType=java.util.Date}
        
		  
            AND TFD.LASTUPDATEDATE = #{lastUpdateDate,javaType=java.util.Date}
        
	
	  
          
            ORDER BY #{orderSql}
        
          
            ORDER BY ID

	SELECT COUNT(1)
	  FROM CORE_FILE_DEFINITION TFD
	
		  
            AND TFD.ID = #{id,javaType=java.lang.String}
        
		  
            AND TFD.SYSTEM = #{system,javaType=java.lang.String}
        
		  
            AND TFD.MODULE = #{module,javaType=java.lang.String}
        
		  
            AND TFD.DELETEDATE = #{deleteDate,javaType=java.util.Date}
        
		  
            AND TFD.RELATIVEPATH = #{relativePath,javaType=java.lang.String}
        
		  
            AND TFD.FILENAMEEXTENSION = #{filenameExtension,javaType=java.lang.String}
        
		  
            AND TFD.FILENAME = #{filename,javaType=java.lang.String}
        
		  
            AND TFD.CREATEDATE = #{createDate,javaType=java.util.Date}
        
		  
            AND TFD.LASTUPDATEDATE = #{lastUpdateDate,javaType=java.util.Date}
