			TD.REMARK,
			TD.NAME,
			TD.FULLNAME,
			TD.CODE,
			TD.TYPE,
			TD.IDCARDCODE
	  FROM BASIC_DISTRICT TD
	
		  
            AND TD.ID = #{id,javaType=java.lang.String}
        
		  
            AND TD.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TD.POSTALCODE = #{postalCode,javaType=java.lang.String}
        
		  
            AND TD.REMARK = #{remark,javaType=java.lang.String}
        
		  
            AND TD.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TD.FULLNAME = #{fullName,javaType=java.lang.String}
        
		  
            AND TD.CODE = #{code,javaType=java.lang.String}
        
		  
            AND TD.TYPE = #{type,javaType=com.tx.component.mainframe.basicdata.DistrictTypeEnum}
        
		  
            AND TD.IDCARDCODE = #{idCardCode,javaType=java.lang.String}
        
	
	  
          
            ORDER BY #{orderSql}

