mapper.select:
	SELECT 
			TD.ID,
			TD.PARENTID,
			TD.DESCRIPTION,
			TD.NAME,
			TD.TYPE
	  FROM BASIC_DISTRICT TD
	
		  
            AND TD.ID = #{id,javaType=java.lang.String}
        
		  
            AND TD.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TD.DESCRIPTION = #{description,javaType=java.lang.String}
        
		  
            AND TD.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TD.TYPE = #{type,javaType=com.tx.component.mainframe.model.DistrictTypeEnum}
        
	
	  
          
            ORDER BY #{orderSql}

	SELECT COUNT(1)
	  FROM BASIC_DISTRICT TD
	
		  
            AND TD.ID = #{id,javaType=java.lang.String}
        
		  
            AND TD.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TD.DESCRIPTION = #{description,javaType=java.lang.String}
        
		  
            AND TD.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TD.TYPE = #{type,javaType=com.tx.component.mainframe.model.DistrictTypeEnum}
