SELECT TO_.ID, TO_.MAINPOSTID AS mainPost_id, TO_.VALID, TO_.PWDERRCOUNT, TO_.HISTORYPWD, TO_.INVALIDDATE, TO_.PASSWORD, TO_.LASTUPDATEDATE, TO_.ORGANIZATIONID AS organization_id, TO_.PWDUPDATEDATE, TO_.USERNAME, TO_.LOCKED, TO_.CREATEDATE, TO_.EXAMINEPWD, TO_.LOGINNAME FROM OPER_OPERATOR TO_ WHERE
AND TO_.ID = #{id}
AND TO_.LOGINNAME = #{loginName}
AND TO_.PASSWORD = #{password}

	SELECT 
			TO_.ID,
			TO_.MAINPOSTID AS mainPost_id,
			TO_.VALID,
			TO_.PWDERRCOUNT,
			TO_.HISTORYPWD,
			TO_.INVALIDDATE,
			TO_.PASSWORD,
			TO_.LASTUPDATEDATE,
			TO_.ORGANIZATIONID AS organization_id,
			TO_.PWDUPDATEDATE,
			TO_.USERNAME,
			TO_.LOCKED,
			TO_.CREATEDATE,
			TO_.EXAMINEPWD,
			TO_.LOGINNAME
	  FROM OPER_OPERATOR TO_
	
		  
            AND TO_.ID = #{id,javaType=java.lang.String}
        
		
			  
	            AND TO_.MAINPOSTID = #{mainPost.id,javaType=java.lang.String}
	        
        
		  
            AND TO_.VALID = #{valid,javaType=boolean}
        
		  
            AND TO_.PWDERRCOUNT = #{pwdErrCount,javaType=java.lang.Integer}
        
		  
            AND TO_.HISTORYPWD = #{historyPwd,javaType=java.lang.String}
        
		  
            AND TO_.INVALIDDATE = #{invalidDate,javaType=java.util.Date}
        
		  
            AND TO_.PASSWORD = #{password,javaType=java.lang.String}
        
		  
            AND TO_.LASTUPDATEDATE = #{lastUpdateDate,javaType=java.util.Date}
        
		
			  
	            AND TO_.ORGANIZATIONID = #{organization.id,javaType=java.lang.String}
	        
        
		  
            AND TO_.PWDUPDATEDATE = #{pwdUpdateDate,javaType=java.util.Date}
        
		  
            AND TO_.USERNAME = #{userName,javaType=java.lang.String}
        
		  
            AND TO_.LOCKED = #{locked,javaType=boolean}
        
		  
            AND TO_.CREATEDATE = #{createDate,javaType=java.util.Date}
        
		  
            AND TO_.EXAMINEPWD = #{examinePwd,javaType=java.lang.String}
        
		  
            AND TO_.LOGINNAME = #{loginName,javaType=java.lang.String}
        
	
	  
          
            ORDER BY #{orderSql}

	SELECT COUNT(1)
	  FROM OPER_OPERATOR TO_
	
		  
            AND TO_.ID = #{id,javaType=java.lang.String}
        
		
			  
	            AND TO_.MAINPOSTID = #{mainPost.id,javaType=java.lang.String}
	        
        
		  
            AND TO_.VALID = #{valid,javaType=boolean}
        
		  
            AND TO_.PWDERRCOUNT = #{pwdErrCount,javaType=java.lang.Integer}
        
		  
            AND TO_.HISTORYPWD = #{historyPwd,javaType=java.lang.String}
        
		  
            AND TO_.INVALIDDATE = #{invalidDate,javaType=java.util.Date}
        
		  
            AND TO_.PASSWORD = #{password,javaType=java.lang.String}
        
		  
            AND TO_.LASTUPDATEDATE = #{lastUpdateDate,javaType=java.util.Date}
        
		
			  
	            AND TO_.ORGANIZATIONID = #{organization.id,javaType=java.lang.String}
	        
        
		  
            AND TO_.PWDUPDATEDATE = #{pwdUpdateDate,javaType=java.util.Date}
        
		  
            AND TO_.USERNAME = #{userName,javaType=java.lang.String}
        
		  
            AND TO_.LOCKED = #{locked,javaType=boolean}
        
		  
            AND TO_.CREATEDATE = #{createDate,javaType=java.util.Date}
        
		  
            AND TO_.EXAMINEPWD = #{examinePwd,javaType=java.lang.String}
        
		  
