SELECT TO.ID, TO.MAINPOSTID AS mainPost_id, TO.VALID, TO.PWDERRCOUNT, TO.HISTORYPWD, TO.INVALIDDATE, TO.PASSWORD, TO.LASTUPDATEDATE, TO.ORGANIZATIONID AS organization_id, TO.PWDUPDATEDATE, TO.USERNAME, TO.LOCKED, TO.CREATEDATE, TO.EXAMINEPWD, TO.LOGINNAME FROM OPER_OPERATOR TO WHERE
AND TO.ID = #{id}
AND TO.LOGINNAME = #{loginName}
AND TO.PASSWORD = #{password}

	SELECT 
			TO.ID,
			TO.MAINPOSTID AS mainPost_id,
			TO.VALID,
			TO.PWDERRCOUNT,
			TO.HISTORYPWD,
			TO.INVALIDDATE,
			TO.PASSWORD,
			TO.LASTUPDATEDATE,
			TO.ORGANIZATIONID AS organization_id,
			TO.PWDUPDATEDATE,
			TO.USERNAME,
			TO.LOCKED,
			TO.CREATEDATE,
			TO.EXAMINEPWD,
			TO.LOGINNAME
	  FROM OPER_OPERATOR TO
	
		  
            AND TO.ID = #{id,javaType=java.lang.String}
        
		
			  
	            AND TO.MAINPOSTID = #{mainPost.id,javaType=java.lang.String}
	        
        
		  
            AND TO.VALID = #{valid,javaType=boolean}
        
		  
            AND TO.PWDERRCOUNT = #{pwdErrCount,javaType=java.lang.Integer}
        
		  
            AND TO.HISTORYPWD = #{historyPwd,javaType=java.lang.String}
        
		  
            AND TO.INVALIDDATE = #{invalidDate,javaType=java.util.Date}
        
		  
            AND TO.PASSWORD = #{password,javaType=java.lang.String}
        
		  
            AND TO.LASTUPDATEDATE = #{lastUpdateDate,javaType=java.util.Date}
        
		
			  
	            AND TO.ORGANIZATIONID = #{organization.id,javaType=java.lang.String}
	        
        
		  
            AND TO.PWDUPDATEDATE = #{pwdUpdateDate,javaType=java.util.Date}
        
		  
            AND TO.USERNAME = #{userName,javaType=java.lang.String}
        
		  
            AND TO.LOCKED = #{locked,javaType=boolean}
        
		  
            AND TO.CREATEDATE = #{createDate,javaType=java.util.Date}
        
		  
            AND TO.EXAMINEPWD = #{examinePwd,javaType=java.lang.String}
        
		  
            AND TO.LOGINNAME = #{loginName,javaType=java.lang.String}
        
	
	  
          
            ORDER BY #{orderSql}

	SELECT COUNT(1)
	  FROM OPER_OPERATOR TO
	
		  
            AND TO.ID = #{id,javaType=java.lang.String}
        
		
			  
