mapper.select:
	SELECT 
			TO.ID,
			TO.PROVINCEID,
			TO.VALID,
			TO.FULLADDRESS,
			TO.ALIAS,
			TO.CITYID,
			TO.CODE,
			TO.TYPE,
			TO.AREAID,
			TO.PARENTID,
			TO.ADDRESS,
			TO.CHIEFTYPE,
			TO.DESCRIPTION,
			TO.NAME,
			TO.FULLNAME,
			TO.CHIEFID
	  FROM OPER_ORGANIZATION TO
	
		  
            AND TO.ID = #{id,javaType=java.lang.String}
        
		  
            AND TO.PROVINCEID = #{provinceId,javaType=java.lang.String}
        
		  
            AND TO.VALID = #{valid,javaType=boolean}
        
		  
            AND TO.FULLADDRESS = #{fullAddress,javaType=java.lang.String}
        
		  
            AND TO.ALIAS = #{alias,javaType=java.lang.String}
        
		  
            AND TO.CITYID = #{cityId,javaType=java.lang.String}
        
		  
            AND TO.CODE = #{code,javaType=java.lang.String}
        
		  
            AND TO.TYPE = #{type,javaType=com.tx.component.operator.model.OrganizationType}
        
		  
            AND TO.AREAID = #{areaId,javaType=java.lang.String}
        
		  
            AND TO.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TO.ADDRESS = #{address,javaType=java.lang.String}
        
		  
            AND TO.CHIEFTYPE = #{chiefType,javaType=int}
        
		  
            AND TO.DESCRIPTION = #{description,javaType=java.lang.String}
        
		  
            AND TO.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TO.FULLNAME = #{fullName,javaType=java.lang.String}
        
		  
            AND TO.CHIEFID = #{chiefId,javaType=int}
        
	
	  
          
            ORDER BY #{orderSql}

	SELECT COUNT(1)
	  FROM OPER_ORGANIZATION TO
	
		  
            AND TO.ID = #{id,javaType=java.lang.String}
        
		  
            AND TO.PROVINCEID = #{provinceId,javaType=java.lang.String}
        
		  
            AND TO.VALID = #{valid,javaType=boolean}
        
		  
