mapper.select:
	SELECT 
			TPG.ID,
			TPG.VALID,
			TPG.REMARK,
			TPG.NAME,
			TPG.CODE
	  FROM POSTGROUP TPG
	
		  
            AND TPG.ID = #{id,javaType=java.lang.String}
        
		  
            AND TPG.VALID = #{valid,javaType=boolean}
        
		  
            AND TPG.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TPG.CODE = #{code,javaType=java.lang.String}
        
	
	  
          
            ORDER BY #{orderSql}

	SELECT COUNT(1)
	  FROM POSTGROUP TPG
	
		  
            AND TPG.ID = #{id,javaType=java.lang.String}
        
		  
            AND TPG.VALID = #{valid,javaType=boolean}
        
		  
            AND TPG.REMARK = #{remark,javaType=java.lang.String}
        
		  
            AND TPG.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TPG.CODE = #{code,javaType=java.lang.String}
