mapper.select:
	SELECT 
			TP.ID,
			TP.VALID,
			TP.PARENTID,
			TP.ORGANIZATIONID AS organization_id,
			TP.REMARK,
			TP.NAME,
			TP.FULLNAME,
			TP.CODE
	  FROM OPER_POST TP
	
		  
            AND TP.ID = #{id,javaType=java.lang.String}
        
		  
            AND TP.VALID = #{valid,javaType=boolean}
        
		  
            AND TP.PARENTID = #{parentId,javaType=java.lang.String}
        
		
			  
	            AND TP.ORGANIZATIONID = #{organization.id,javaType=java.lang.String}
	        
        
		  
            AND TP.REMARK = #{remark,javaType=java.lang.String}
        
		  
            AND TP.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TP.FULLNAME = #{fullName,javaType=java.lang.String}
        
		  
            AND TP.CODE = #{code,javaType=java.lang.String}
        
	
	  
          
            ORDER BY #{orderSql}

	SELECT COUNT(1)
	  FROM OPER_POST TP
	
		  
            AND TP.ID = #{id,javaType=java.lang.String}
        
		  
            AND TP.VALID = #{valid,javaType=boolean}
        
		  
            AND TP.PARENTID = #{parentId,javaType=java.lang.String}
        
		
			  
	            AND TP.ORGANIZATIONID = #{organization.id,javaType=java.lang.String}
	        
        
		  
            AND TP.REMARK = #{remark,javaType=java.lang.String}
        
		  
            AND TP.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TP.FULLNAME = #{fullName,javaType=java.lang.String}
        
		  
            AND TP.CODE = #{code,javaType=java.lang.String}
