mapper.select:
  
         
			SELECT DISTINCT TAR.AUTHID,
			                TAR.REFID,
			                TAR.AUTHREFTYPE,
			                TAR.CREATEDATE,
			                TAR.ENDDATE,
			                TAR.CREATEOPERID
			  from t_auth_authref tar
			 where tar.authType = #{authType}
			   and tar.refid = #{refId}
			   and tar.operid = #{operId}

mapper.delete:
delete from t_auth_authref
 where refId = #{refId}
   and authRefType = #{authRefType}
