SELECT TPI.ID, TPI.IDCARDNUMBER, TPI.INAME, TPI.CURRENTPAGEINDEX, TPI.REGDATE, TPI.EXPORTED, TPI.EXPORTEDDATE, TPI.FETCHDATE, TPI.PAGEINDEX, TPI.DISRUPTTYPENAME, TPI.SEXY, TPI.CASECODE, TPI.AREANAME, TPI.COURTNAME, TPI.DUTY, TPI.PARTYTYPENAME, TPI.GISTUNIT, TPI.PERFORMANCE, TPI.AGE, TPI.GISTID, TPI.PUBLISHDATE, TPI.CARDNUM, TPI.FOCUSNUMBER FROM FETCH_PERSON_INFO TPI WHERE
AND TPI.ID = #{id}
AND TPI.IDCARDNUMBER = #{idCardNumber}
AND TPI.CARDNUM = #{idCardNumber} AND TPI.INAME = #{iname}

select DISTINCT TPI.IDCARDNUMBER, TPI.INAME FROM FETCH_PERSON_INFO TPI
AND TPI.exported = #{exported,javaType=boolean}
AND TPI.IDCARDNUMBER is not null
ORDER BY #{orderSql}
ORDER BY IDCARDNUMBER

SELECT COUNT(1) FROM ( SELECT DISTINCT TPI.IDCARDNUMBER, TPI.INAME FROM FETCH_PERSON_INFO TPI
AND TPI.EXPORTED = #{exported,,javaType=boolean}
AND TPI.IDCARDNUMBER is not null
) TTPI

	SELECT 
