            AND TPI.REGDATE = #{regDate,javaType=java.lang.String}
        
		  
            AND TPI.DISRUPTTYPENAME = #{disruptTypeName,javaType=java.lang.String}
        
		  
            AND TPI.SEXY = #{sexy,javaType=java.lang.String}
        
		  
            AND TPI.CASECODE = #{caseCode,javaType=java.lang.String}
        
		  
            AND TPI.AREANAME = #{areaName,javaType=java.lang.String}
        
		  
            AND TPI.DUTY = #{duty,javaType=java.lang.String}
        
		  
            AND TPI.PARTYTYPENAME = #{partyTypeName,javaType=java.lang.String}
        
		  
            AND TPI.COURTNAME = #{courtName,javaType=java.lang.String}
        
		  
            AND TPI.PERFORMANCE = #{performance,javaType=java.lang.String}
        
		  
            AND TPI.GISTUNIT = #{gistUnit,javaType=java.lang.String}
        
		  
            AND TPI.AGE = #{age,javaType=java.lang.String}
        
		  
            AND TPI.GISTID = #{gistId,javaType=java.lang.String}
        
		  
            AND TPI.PUBLISHDATE = #{publishDate,javaType=java.lang.String}
        
		  
            AND TPI.CARDNUM = #{cardNum,javaType=java.lang.String}
        
		  
            AND TPI.FOCUSNUMBER = #{focusNumber,javaType=java.lang.String}
        
	
	  
          
            ORDER BY #{orderSql}
        
          
            ORDER BY ID

