SELECT TD.ID, TD.TESTBOOLEANOBJ, TD.TESTNULLBOOLEANOBJ, TD.TESTINT, TD.TESTINTEGEROBJ, TD.TESTBOOLEAN, TD.PASSWORD, TD.LASTUPDATEDATE, TD.PARENTID, TD.EMAIL, TD.DESCRIPTION, TD.NAME, TD.SUBDEMOID AS subDemo_id, TD.TESTNULLINTEGEROBJ, TD.TESTNULLBIGDECIMAL, TD.CREATEDATE, TD.TESTBIGDECIMAL, TD.ISVALID, TD.LOGINNAME FROM WD_DEMO TD WHERE
AND TD.ID = #{id}
AND TD.LOGINNAME = #{loginName}

	SELECT 
			TD.ID,
			TD.TESTBOOLEANOBJ,
			TD.TESTNULLBOOLEANOBJ,
			TD.TESTINT,
			TD.TESTINTEGEROBJ,
			TD.TESTBOOLEAN,
