mapper.select:
	SELECT 
			TW.ID,
			TW.PARENTID,
			TW.EMAIL,
			TW.DESCRIPTION,
			TW.NAME,
			TW.SUBDEMOID AS subDemo_id,
			TW.CREATEDATE,
			TW.PASSWORD,
			LOGINNAME AS loginName,
			TW.LASTUPDATEDATE,
			TW.ISVALID
	  FROM WD_DEMO TW
	
		  
            AND TW.ID = #{id}
        
		  
            AND TW.PARENTID = #{parentId}
        
		  
            AND TW.EMAIL = #{email}
        
		  
            AND TW.DESCRIPTION = #{description}
        
		  
            AND TW.NAME = #{name}
        
		
			  
	            AND TW.SUBDEMOID = #{subDemo.id}
	        
        
		  
            AND TW.CREATEDATE = #{createDate}
        
		  
            AND TW.PASSWORD = #{password}
        
		  
            AND TW.ISVALID = #{isValid}
        
		  
            AND TW.LOGINNAME = #{loginName}
        
		  
            AND TW.LASTUPDATEDATE = #{lastUpdateDate}
        
	
	  
          
            ORDER BY #{orderSql}

	SELECT COUNT(1)
	  FROM WD_DEMO TW
	
		  
            AND TW.ID = #{id}
        
		  
            AND TW.PARENTID = #{parentId}
        
		  
            AND TW.EMAIL = #{email}
        
		  
            AND TW.DESCRIPTION = #{description}
        
		  
            AND TW.NAME = #{name}
        
		
			  
	            AND TW.SUBDEMOID = #{subDemo.id}
	        
        
		  
            AND TW.CREATEDATE = #{createDate}
        
		  
            AND TW.PASSWORD = #{password}
        
		  
            AND TW.ISVALID = #{isValid}
        
		  
            AND TW.LOGINNAME = #{loginName}
        
		  
            AND TW.LASTUPDATEDATE = #{lastUpdateDate}
